package com.first_java;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		  int x = 10;  
		  int y = 20;    
		  x = x + y;  
		  y = x - y;  
		  x = x - y;  
		  System.out.println("value of x:" + x);  
		  System.out.println("value of y:" + y);  
	}

}
